import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{o as i,c as e,f as a}from"./app-a98e131c.js";const r={},t=a('<h1 id="数据库概述" tabindex="-1"><a class="header-anchor" href="#数据库概述" aria-hidden="true">#</a> 数据库概述</h1><h2 id="为什么使用数据库" tabindex="-1"><a class="header-anchor" href="#为什么使用数据库" aria-hidden="true">#</a> 为什么使用数据库</h2><ul><li>持久化 <ul><li>把数据保存到可掉电式存储设备中，供以后使用</li><li>企业级应用，将内存中的数据保存到硬盘上</li><li>持久化作用 <ul><li>将内存中的数据存储在关系型数据库中，或者 磁盘文件、XML 数据文件中</li></ul></li></ul></li></ul><h2 id="相关概念" tabindex="-1"><a class="header-anchor" href="#相关概念" aria-hidden="true">#</a> 相关概念</h2><ul><li>DB：数据库（Database） <ul><li>即存储数据的“仓库”，其本质是一个文件系统。它保存了一系列有组织的数据。</li></ul></li><li>DBMS：数据库管理系统（Database Management System） <ul><li>是一种操纵和管理数据库的大型软件，用于建立、使用和维护数据库，对数据库进行统一管理和控制。用户通过数据库管理系统访问数据库中表内的数据。</li></ul></li><li>SQL：结构化查询语言（Structured Query Language） <ul><li>专门用来与数据库通信的语言</li></ul></li><li>DB 与 DBMS <ul><li>数据库管理系统(DBMS)可以管理多个数据库，一般开发人员会针对每一个应用创建一个数据库。</li><li>为保存应用中实体的数据，一般会在数据库创建多个表，以保存程序中实体用户的数据</li></ul></li></ul><h2 id="常见的数据库管理系统排名-dbms" tabindex="-1"><a class="header-anchor" href="#常见的数据库管理系统排名-dbms" aria-hidden="true">#</a> 常见的数据库管理系统排名(DBMS)</h2><ul><li>Oracle</li><li>MySQL</li><li>Microsoft SQL Server</li><li>PostgreSQL</li><li>MongoDB</li><li>Redis</li><li>IBM DB2</li><li>Elasticsearch</li><li>SQLite</li></ul>',7),s=[t];function d(n,u){return i(),e("div",null,s)}const o=l(r,[["render",d],["__file","intro.html.vue"]]);export{o as default};
